	export async function load(session) {
		if (!browser && !process.env.VITE_GITHUB_APP_CLIENTID) {
			return {
				status: 302,
				redirect: '/dashboard'
			};
		}
		return {
			props: {
				initDashboard: await request('/api/v1/dashboard', session)
			}
		};
	}
</script>

